CREATE TABLE CW_PRIORITY (ID bigint, NAME varchar(50), COLOR_CODE Text, PRIMARY KEY(ID, NAME));
CREATE TABLE CW_STATUS (ID bigint, NAME varchar(50), PRIMARY KEY(ID, NAME));
CREATE TABLE CW_ORGANIZATION_DETAILS(ID INTEGER PRIMARY KEY AUTOINCREMENT, NAME Text, OWNERID bigint);
CREATE TABLE CW_TEAMSPACE_DETAILS(ID INTEGER PRIMARY KEY AUTOINCREMENT, NAME Text, ORGANIZATIONID bigint, OWNERID, bigint, FOREIGN KEY(ORGANIZATIONID) REFERENCES CW_ORGANIZATION_DETAILS(ID));
CREATE TABLE CW_PROJECT_DETAILS (ID INTEGER PRIMARY KEY AUTOINCREMENT, NAME Text, STATUS bigint, PRIORITY bigint, TEAMSPACEID bigint, OWNERID bigint, FOREIGN KEY(STATUS) REFERENCES CW_STATUS(ID), FOREIGN KEY(PRIORITY) REFERENCES CW_PRIORITY(ID));
CREATE TABLE CW_TASK_DETAILS (ID INTEGER PRIMARY KEY AUTOINCREMENT, NAME Text, DESCRIPTION Text, STATUS bigint, PRIORITY bigint, PROJECTID bigint, OWNERID bigint, ASSIGNEEID bigint, PARENT_TASK_ID bigint, FOREIGN KEY(PROJECTID) REFERENCES CW_PROJECT_DETAILS(ID), FOREIGN KEY(STATUS) REFERENCES CW_STATUS(ID), FOREIGN KEY(PRIORITY) REFERENCES CW_PRIORITY(ID));
CREATE TABLE CW_COMMENT_DETAILS(ID INTEGER PRIMARY KEY AUTOINCREMENT, MESSAGE Text, TASK_ID bigint, OWNER_ID bigint, FOREIGN KEY(TASK_ID) REFERENCES CW_TASK_DETAILS(ID));
CREATE TABLE CW_ATTACHMENT_DETAILS (ID INTEGER PRIMARY KEY AUTOINCREMENT, NAME Text, PATH Text, TYPE INTEGER, TASK_ID bigint, COMMENT_ID bigint, FOREIGN KEY(TASK_ID) REFERENCES CW_TASK_DETAILS(ID), FOREIGN KEY(COMMENT_ID) REFERENCES CW_COMMENT_DETAILS(ID));

