CREATE TABLE CW_ORGANIZATION_DETAILS(ID INTEGER PRIMARY KEY AUTOINCREMENT, NAME Text, OWNERID bigint);
CREATE TABLE CW_TEAMSPACE(ID INTEGER PRIMARY KEY AUTOINCREMENT, NAME Text, ORGANIZATIONID bigint, OWNERID, bigint, FOREIGN KEY(ORGANIZATIONID) REFERENCES CW_ORGANIZATION_DETAILS(ID));
CREATE TABLE CW_PROJECT_DETAILS (ID INTEGER PRIMARY KEY AUTOINCREMENT, NAME Text, STATUS INTEGER, PRIORITY INTEGER, TEAMSPACEID bigint, OWNERID bigint);
CREATE TABLE CW_TASK_DETAILS (ID INTEGER PRIMARY KEY AUTOINCREMENT, NAME Text, DESCRIPTION Text, STATUS INTEGER, PRIORITY INTEGER, PROJECTID bigint, OWNERID bigint, ASSIGNEEID bigint, FOREIGN KEY(PROJECTID) REFERENCES CW_PROJECT_DETAILS(ID));
CREATE TABLE CW_TASKRELATIONMAPPER (PARENT_TASK_ID bigint, CHILD_TASK_ID bigint, HEIRARCHY_LEVEL bigint, FOREIGN KEY(PARENT_TASK_ID) REFERENCES CW_TASK_DETAILS(ID), FOREIGN KEY(CHILD_TASK_ID) REFERENCES CW_TASK_DETAILS(ID));